def getModelFile(path):#获取所有文件的路径
    filelist = []
    if os.path.isfile(path):
        filelist.append(path)
        return filelist
    elif os.path.isdir(path):
        for s in os.listdir(path):
            newpath = os.path.join(path,s)
            filelist += getModelFile(newpath)
        return filelist

def filterFileList(file_list,ext_list):#过滤文件列表
    filelist = []
    for file in file_list:
        filename,extname = os.path.splitext(file)
        filename=os.path.split(filename)[1]
        #print(filename)
        if extname.lower() in ext_list and re.match("~\$",filename)==None:
            filelist.append(file)
    return filelist

def runStep(path):
    noext = os.path.splitext(path)[0]
    filename = os.path.split(noext)[1]
    tpath = WORKPATH + str('\\screen\\')+str(filename)+str('.png')
    print(tpath)
    if os.path.exists(tpath):
        return True
    else:
        return False


def loadJson(path):
    with open(path,'r',encoding="utf-8") as f:
        obj = json.load(f)
    return obj


def main():
    global RENDICT
    RENDICT = loadJson(WORKPATH+"\\config")
    filelist = RENDICT.keys()
    global FILELIST
    FILELIST = filelist
    for m in filelist:
        if runStep(m):
            continue
        render = ModelRender(m)
        render.setEnviroment()
        render.setMaterials()
        time.sleep(3)
        render.render_image()
        time.sleep(1)
